        loading.value = false;
      }
    };
    const __returned__ = {
      username,
      password,
      loading,
      errorMessage,
      submitLogin,
      ref,
      get axios() {
        return axios;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
