import "core-js/modules/es.array.push.js";
import { ref } from "vue";
import axios from "axios";
import router from "../router"; // สมมติคุณใช้ vue-router

export default {
  __name: 'LoginPage',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const email = ref("");
    const password = ref("");
    const error = ref("");
    const login = async () => {
      try {
        const response = await axios.post("http://localhost:8000/users/login", {
          email: email.value,
          password: password.value
        });
        // เก็บ token ไว้ใน localStorage
        localStorage.setItem("token", response.data.access_token);
        router.push("/dashboard");
      } catch (err) {
        error.value = err.response?.data?.detail || "Login failed";
      }
    };
    const __returned__ = {
      email,
      password,
      error,
      login,
      ref,
      get axios() {
        return axios;
      },
      get router() {
        return router;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
