import { ref } from 'vue';
import axios from 'axios';
export default {
  __name: 'Login',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const username = ref('');
    const password = ref('');
    const valid = ref(false);
    const errorMessage = ref('');
    const loading = ref(false);
    const submitLogin = async () => {
      errorMessage.value = '';
      if (!valid.value) return;
      loading.value = true;
      try {
        const response = await axios.post('http://localhost:8000/login', {
          username: username.value,
          password: password.value
        });
        console.log('Login success:', response.data);
        // เก็บ token หรือทำอย่างอื่นตามต้องการ เช่น redirect ไปหน้าอื่น
      } catch (error) {
        errorMessage.value = error.response?.data?.detail || 'Login failed';
      } finally {
        loading.value = false;
      }
    };
    const __returned__ = {
      username,
      password,
      valid,
      errorMessage,
      loading,
      submitLogin,
      ref,
      get axios() {
        return axios;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
